import myDataES from "../Language/myData-ES.json";
import myDataEN from "../Language/myData-EN.json";
import { useState } from "react";

const INITIAL_STATE = {
  spanish: myDataES,
  english: myDataEN,
};

export const useMyData = () => {
  
  const [idioma, setIdioma] = useState(INITIAL_STATE.spanish)



  const changeLenguage = () => {
    if (idioma.lenguage === "ES") {
      setIdioma(INITIAL_STATE.english)
    } else {
      setIdioma(INITIAL_STATE.spanish)
    }
  };

  /* TODO: Implementar metodo para que determinado el caso, me devuelva
    el .Json en ingles o en español... Aplicar Context seguramente
*/
  return {
    ...idioma ,
    changeLenguage,
  };
};
