import myDataES from "../Language/myData-ES.json";
import myDataEN from "../Language/myData-EN.json";
import { useState } from "react";

const INITIAL_STATE = {
  spanish: myDataES,
  english: myDataEN,
};

export const useMyData = () => {
  const lenguage = localStorage.getItem("leng");
  console.log(lenguage)

  const [idioma, setIdioma ] = useState(INITIAL_STATE.spanish)


  if (lenguage === "ES") {
    setIdioma(INITIAL_STATE.english);
  } else {
    setIdioma(INITIAL_STATE.spanish);
  }

  // console.log("diiodas", idioma.lenguage)

  const asd: any ={
    ES: setIdioma(INITIAL_STATE.spanish),
    EN: setIdioma(INITIAL_STATE.english)
  }


  const changeLenguage = () => {
    asd[lenguage]
  };
      // localStorage.setItem("leng", "EN");

  return {
    ...idioma,
    changeLenguage,
  };
};
