import React from "react";
import PortfolioCSS from "../Portfolio/Portfolio.module.css";
import ModalContainerCSS from "./ModalContainer.module.css"

interface Props {
  children: string | any;
  validation: any;
}

export const ModalContainer: React.FC<Props> = (props) => {
  const { children, validation } = props;
  const CssOpenModal = `${ModalContainerCSS.portfolioModal} ${ModalContainerCSS.openPortafolioModal}`;

  return (
    <article
      className={validation ? CssOpenModal : `${ModalContainerCSS.portfolioModal}`}
    >
      {children}
    </article>
  );
};
