import { Fragment, useEffect, useState } from "react";

import "./App.css";
import { Header } from "./Components/Header/Header";
import { Home } from "./Components/Inicio/Home";
import { AboutMe } from "./Components/AboutMe/AboutMe";
import { Portfolio } from "./Components/Portfolio/Portfolio";
import { Footer } from "./Components/Footer/Footer";
import { Contact } from "./Components/Contact/Contact";
import {
  IBeforeInstallPromptEvent,
  useAddToHomeScreenPrompt,
} from "./Hooks/useAddToHomeScreenPrompt";

// localStorage.setItem("leng", "ES")

function App() {
  // const { isReady, promptToInstall, finishInstallation } =
  //   useAddToHomeScreenPrompt();
  window.onload = async (e) => {
    let deferredPrompt: any;
    window.addEventListener("beforeinstallprompt", (e) => {
      // Prevent Chrome 67 and earlier from automatically showing the prompt
      e.preventDefault();
      // Stash the event so it can be triggered later.
      deferredPrompt = e;
    });
    // Show the prompt
    deferredPrompt.prompt();
    // Wait for the user to respond to the prompt
    const { outcome } = await deferredPrompt.userChoice;

    if (outcome === "accepted") {
      console.log("User accepted the A2HS prompt");
    } else {
      console.log("User dismissed the A2HS prompt");
    }
    deferredPrompt = null;
  };

  return (
    <Fragment>
      <Header />

      <main>
        <Home />
        <div className="App">
          <p>Instala esta aplicación en tu dispositivo</p>
          {/* <p>
            {isReady && !finishInstallation && (
              <button onClick={() => promptToInstall()}>Instalar</button>
            )}
          </p>
          <p>
            {finishInstallation
              ? "Estás usando la Web App"
              : "Aplicación todavía no instalada"}
          </p> */}
        </div>

        <AboutMe />

        <Portfolio />

        <Contact />

        <Footer />
      </main>
    </Fragment>
  );
}

export default App;
