import myDataES from "../Language/myData-ES.json";
import myDataEN from "../Language/myData-EN.json";
import { useState } from "react";

const INITIAL_STATE = {
  spanish: myDataES,
  english: myDataEN,
};

export const useMyData = () => {
  const lenguage = localStorage.getItem("leng");

  console.log(lenguage === "ES");

  let idioma = INITIAL_STATE.english;

  if (lenguage === "ES") {
    idioma = INITIAL_STATE.english;
  } else {
    idioma = INITIAL_STATE.spanish;
  }


  return {
    ...idioma,
    // changeLenguage,
  };
};
