import { Fragment } from "react";

import "./App.css";
import { Header } from "./Components/Header/Header";
import { Home } from "./Components/Inicio/Home";
import { AboutMe } from "./Components/AboutMe/AboutMe";
import { Portfolio } from "./Components/Portfolio/Portfolio";
import { Footer } from "./Components/Footer/Footer";
import { Contact } from "./Components/Contact/Contact";

// localStorage.setItem("leng", "ES")
import { useAddToHomeScreenPrompt } from "./Hooks/useAddToHomeScreenPrompt";
import { AddToHomeScreen } from "react-pwa-add-to-homescreen";

function App() {
  const { isReady, promptToInstall, finishInstallation } =
    useAddToHomeScreenPrompt();

  return (
    <Fragment>
      <Header />

      <main>
        <Home />

        <AddToHomeScreen
          skipFirstVisit={false}
          translate={{
            headline: "Puede instalar esta pagina en su escritorio",
            bottomline: "Aceptar",
            buttonInstall: "aceptar asdas ",
          }}
          styles={
            {
              body: {
                background: "red"
              },
              button: {
                backgroundColor: "orange"
              },
              heading: {
                background: "green"
              }

            }
          }
        />
        {/* <div className="App">
          <p>Instala esta aplicación en tu dispositivo</p>
          <p>
            {isReady && !finishInstallation && (
              <button onClick={() => promptToInstall()}>Instalar</button>
            )}
          </p>
          <p>
            {finishInstallation
              ? "Estás usando la Web App"
              : "Aplicación todavía no instalada"}
          </p>
        </div> */}

        <AboutMe />

        <Portfolio />

        <Contact />

        <Footer />
      </main>
    </Fragment>
  );
}

export default App;
