import { useCallback, useEffect, useMemo, useState } from "react";

interface IBeforeInstallPromptEvent extends Event {
  readonly platforms: string[];
  readonly userChoice: Promise<{
    outcome: "accepted" | "dismissed";
    platform: string;
  }>;
  prompt(): Promise<void>;
}
/// Capta si tenemos la PWA instalada.
const isInWebApp = window.matchMedia("(display-mode: standalone)").matches;

export function useAddToHomeScreenPrompt() {
  /* CODIGO SACADO DE: https://www.youtube.com/watch?v=3f20qBfACzI
   No entendi su funcionamiento pero anda.
  
    Practicamente lo que hace es: Si al ingresar a la pagina, no tenes 
    la app instalada, te aparece una ventanita para isntalar
    caso contrario, si lo tenes instalado, no te aparece nada.
  */

  const [prompt, setPrompt] = useState<IBeforeInstallPromptEvent | null>(null);
  const [isReady, setIsReady] = useState(false);
  const [finishInstallation, setFinishInstallation] = useState(isInWebApp);

  // Hace aparecer la ventana para isntalar
  const promptToInstall = useCallback(async () => {
    if (prompt) {
      prompt.prompt();

      const choiceResult = await prompt.userChoice;
      if (choiceResult.outcome === "accepted") {
        setFinishInstallation(true);
      }

      return;
    }

    return Promise.reject(
      new Error(
        'Tried installing before browser sent "beforeinstallprompt" event'
      )
    );
  }, [prompt]);

  useEffect(() => {
    if (prompt) {
      setIsReady(true);
    }
  }, [prompt]);

  function ready(e: IBeforeInstallPromptEvent) {
    e.preventDefault();
    setPrompt(e);
  }

  useEffect(() => {
    window.addEventListener("beforeinstallprompt", ready as any);

    return function () {
      window.removeEventListener("beforeinstallprompt", ready as any);
    };
  }, []);

  return useMemo(
    () => ({
      isReady,
      promptToInstall,
      finishInstallation,
    }),
    [isReady, promptToInstall, finishInstallation]
  );
}
